import { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { cartActions } from './store/myCart';
import Notification from './components/UI/Notification';

let isInitial = true;

function App() {
  const dispatch = useDispatch();
  const showMyCart = useSelector((state) => state.cart.showMyCart);
  const cart = useSelector((state) => state.cartSlice);
  const notification = useSelector((state) => state.cart.notification);

  useEffect(() => { // useEffects allow us to run side effects and run effects when some dependency changes
    const sendCartData = async () => {
      // dispatch(
      //   cartActions.showNotification({
      //     status: 'pending',
      //     title: 'Sending...',
      //     message: 'Sending cart data!',
      //   })
      // );
      // const response = await fetch('https://react-practice-c0ee8-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json',
      //   {
      //     method: 'PUT',
      //     body: JSON.stringify(cart)
      //   }
      // );

      // if (!response.ok) {
      //   throw new Error('Sending cart data failed');
      // }
      // const responseData = await response.json();

      // dispatch(
      //   cartActions.showNotification({
      //     status: 'success',
      //     title: 'Success!',
      //     message: 'Sent cart data successfully!',
      //   })
      // );
    };

    // this is written so that we don't send our cart at the first time
    if (isInitial) {
      isInitial = false;
      return; // not send the cart data
    }

    // since it's returning a Promise, we can use the catch method
    sendCartData().catch(error => {
      dispatch(
        cartActions.showNotification({
          status: 'error',
          title: 'Error!',
          message: 'Sending cart data failed!',
        })
      );
    });

  }, [cart, dispatch]); // cart as a dependency array so that this effect function re-executes whenever the cart changes

  return (
    <Fragment>
      {notification && <Notification status={notification.status} title={notification.status} message={notification.message} />}
      <Layout>
        {showMyCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
