import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { cartActions } from './store/myCart';

function App() {
  const dispatch = useDispatch();
  const showMyCart = useSelector(state => state.cart.showMyCart);
  const cart = useSelector(state => state.cartSlice);

  useEffect(() => { // useEffects allow us to run side effects and run effects when some dependency changes
    const sendCartData = async () => {
      dispatch(cartActions.showNotification({
        status: 'pending'
      }));
      const response = fetch('https://react-practice-c0ee8-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json',
        {
          method: 'PUT',
          body: JSON.stringify(cart)
        }
      );

      if (!response.ok) {
        throw new Error('Sending cart data failed');
      }
      const responseData = await response.json();

    };
  }, [cart]); // cart as a dependency array so that this effect function re-executes whenever the cart changes

  return (
    <Layout>
      {showMyCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
