import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkit';

const initialState = { counter: 0, showCounter: true };

/* 
 * in redux/toolkit -> we're not overwriting the existing code but instead,
 * clone and create a new state object, keep all the state which we're not editing and override the state which we're editing in an immutable way
 * because of this internally used package
*/
createSlice({ // preparing a slice of our global state
    name: 'counter', // identifier
    initialState: initialState,
    reducers: {
        increment(state) {
            state.counter++;
        },
        decrement(state) {
            state.counter--;
        },
        increase(state, action) {
            state.counter = state.counter + action.amount;
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
        }
    }
})

const counterReducer = (state = initialState, action) => {
    if (action.type === 'increment') {
        return {
            counter: state.counter + 1,
            showCounter: state.showCounter
        };
    }

    if (action.type === 'increase') {
        return {
            counter: state.counter + action.amount,
            showCounter: state.showCounter
        }
    }

    if (action.type === 'decrement') {
        return {
            counter: state.counter - 1,
            showCounter: state.showCounter
        };
    }

    if (action.type === 'toggle') {
        return {
            counter: state.counter,
            showCounter: !state.showCounter
        }
    }

    return state;
}

// create redux store
const store = createStore(counterReducer);

export default store; // export so that we can use logic outside of this file

// subscriber function
const counterSubscriber = () => {
    const latestState = store.getState();
    console.log(latestState);
}

const incrementAction = { type: 'increment' };
const decrementAction = { type: 'decrement' };

// subscribe to store changes
store.subscribe(counterSubscriber);

store.dispatch(incrementAction);
store.dispatch(decrementAction);
