import { createStore } from 'redux';
import { createSlice, configureStore } from '@reduxjs/toolkit';

// Counter
const initialCounterState = {
    counter: 0,
    showCounter: true
};

/* 
 * in redux/toolkit -> we're not overwriting the existing code but instead,
 * clone and create a new state object, keep all the state which we're not editing and override the state which we're editing in an immutable way
 * because of this internally used package
*/
const counterSlice = createSlice({ // preparing a slice of our global state
    name: 'counter', // identifier
    initialState: initialCounterState,
    reducers: {
        increment(state) {
            state.counter++;
        },
        decrement(state) {
            state.counter--;
        },
        increase(state, action) {
            state.counter = state.counter + action.payload;
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
        }
    }
})

// Authentication
const initialAuthState = {
    isAuthenticated: false
};

const authSlice = createSlice({
    name: 'authentication',
    initialState: initialAuthState,
    reducer: {
        login(state) {
            state.isAuthenticated = true;
            console.log('logged in');
        },
        logout(state) {
            state.isAuthenticated = false;
        }
    }
});

/* after creating the `createSlice` method, how do we dispatch it? */

// configureStore method: helps us to combine lots of states into one global reducer
const store = configureStore({
    reducer: { // store has to have only 1 root reducer
        counter: counterSlice.reducer,
        auth: authSlice.reducer
    }
});

export const counterActions = counterSlice.actions;
export const authActions = authSlice.actions;

export default store; // export so that we can use logic outside of this file
