import { Link, useParams, useLoaderData, json } from 'react-router-dom';
import EventItem from '../components/EventItem.js';

function EventDetailPage() {
    const data = useLoaderData();
    const params = useParams();
    console.log(data);

    return (
        <>
            <EventItem event={params.eventId} />
            <p>
                <button>
                    <Link to='..' relative="path">Back</Link>
                </button>
            </p>
        </>

    )
}

export default EventDetailPage;

export async function loader({ request, params }) { // object contains 2 important pieces 1. request, 2. params
    const id = params.eventId;

    const response = await fetch('http://localost:8080/events/' + id);

    if (!response.ok) {
        throw json({ message: 'Could not fetch details' },
            {
                status: 500
            },
        );
    } else {
        return response;
    }
}

